import React, { useState, useEffect } from 'react';
import { View, Image, Dimensions, Button, Text } from 'react-native';
import { auth } from '../FirebaseConfig';

import fondochat from '../../assets/fondochat.png';
import logo500 from '../../assets/logo500.png';

const SignInChat = ({ navigation }) => {
  const [isMember, setIsMember] = useState(false);

  useEffect(() => {
    const checkMembershipStatus = async () => {
      try {
        const user = auth.currentUser;

        // Aquí debes obtener el valor de 'isMember' desde Firestore o Realtime Database.
        // Establece setIsMember(true) si el usuario es miembro.

        // Ejemplo utilizando Firestore:
        // const userDoc = await firestore.collection('users').doc(user.uid).get();
        // setIsMember(userDoc.data()?.isMember);

        // Ejemplo utilizando Realtime Database:
        // const userSnapshot = await database.ref(`users/${user.uid}`).once('value');
        // setIsMember(userSnapshot.val()?.isMember);

        // Simulación (debes implementar la lógica real):
        setIsMember(true);  // Esto debería establecerse según la lógica real.
      } catch (error) {
        console.error('Error checking membership status:', error);
      }
    };

    checkMembershipStatus();
  }, []);

  const handleMemberPress = () => {
    // Aquí decides qué hacer cuando el usuario ya es miembro.
    // Podría ser navegar a la siguiente pantalla del chat.
    navigation.navigate('Chat');
  };

  const handleBecomeMemberPress = () => {
    // Aquí decides qué hacer cuando el usuario no es miembro.
    // Podría ser mostrar un mensaje o navegar a la pantalla para hacerse miembro.
    // Puedes personalizar esta lógica según tus necesidades.
    console.log('Navigate to become a member screen or show a message.');
  };

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'start' }}>
      <Image
        source={fondochat}
        resizeMode='cover'
        style={{ width: Dimensions.get('window').width, height: 200 }}
      />
      <View style={{ flex: 1, backgroundColor: 'white', borderTopLeftRadius: 90, marginTop: -44, alignItems: 'center', paddingTop: 20, paddingHorizontal: 20, }}>
        {isMember ? (
          <Button title="Enter Chat" onPress={handleMemberPress} />
        ) : (
          <>
            <Text>You are not a member yet. Become a member to access the chat.</Text>
            <Button title="Become a Member" onPress={handleBecomeMemberPress} />
          </>
        )}
      </View>
    </View>
  );
};

export default SignInChat;
