import React, { useState } from "react";
import {
  SafeAreaView,
  TouchableOpacity,
  ScrollView,
  TextInput,
  StyleSheet,
  Text,
  View,
  Alert,
  Dimensions, 
  KeyboardAvoidingView, 
  Platform
} from "react-native";
import { Picker } from "@react-native-picker/picker";
import MyBlur from "../components/MyBlur";

import {
  createUserWithEmailAndPassword,
  onAuthStateChanged,
} from "firebase/auth";
import { collection, addDoc, doc, setDoc } from "firebase/firestore";
import { auth, database } from "../../FirebaseConfig";
import PhoneInput from "react-native-phone-input";

const Registration = ({ navigation }) => {
  const { height } = Dimensions.get("window");
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    firstName: "",
    lastName: "",
    phone:"",
    gender: "",
    diasEstrategia: "",
  });

  const handleChangeText = (fieldName, value) => {
    if (fieldName === "phone") {
      setFormData({ ...formData, phone: value });
    } else {
      setFormData({ ...formData, [fieldName]: value });
    }
  };

  const validateForm = () => {
    const errors = {};

    if (formData.password !== formData.confirmPassword) {
      errors.password = "Las contraseñas no coinciden";
    }
    if (!formData.firstName || formData.firstName.length < 2) {
      errors.firstName = "El nombre debe tener al menos 2 caracteres";
    }
    if (!formData.lastName || formData.lastName.length < 2) {
      errors.lastName = "El apellido debe tener al menos 2 caracteres";
    }

    const diasEstrategia = parseInt(formData.diasEstrategia);
    if (!diasEstrategia || isNaN(diasEstrategia)) {
      errors.diasEstrategia = "Este campo debe ser numérico";
    }

    return { isValid: Object.keys(errors).length === 0, errors };
  };

  const handleRegistration = async () => {
    try {
      const countryCode = this.phoneInput.getCountryCode();
      console.log("Registrando usuario en Firebase Auth");
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );

      console.log("Registro exitoso");
      console.log("UID del usuario autenticado:", userCredential.user.uid);

      console.log("Guardando datos adicionales en Firestore");

      // Utilizar el mismo UID del usuario como ID del documento
      const userDocRef = doc(database, "users", userCredential.user.uid);

      await setDoc(userDocRef, {
        email: formData.email,
        firstName: formData.firstName,
        lastName: formData.lastName,
        phone: formData.phone,
        countryCode: countryCode,
        gender: formData.gender,
        diasEstrategia: formData.diasEstrategia,
        userId: userCredential.user.uid,
        videosFavoritos: [],
        isMember: false,  // Agrega el campo isMember con valor por defecto
      });

      console.log("Registro en Firestore exitoso");

      // Puedes agregar lógica adicional aquí si es necesario

      Alert.alert(
        "Registro Exitoso",
        "Por favor, revise su correo electrónico para completar el proceso de registro.",
        [
          {
            text: "Ok",
            onPress: () => {
              // Redirigir al usuario a la pantalla de inicio de sesión
              navigation.navigate("SignIn");
            },
          },
        ]
      );
    } catch (error) {
      if (error.code === "auth/email-already-in-use") {
        console.log("La dirección de correo electrónico ya está en uso.");
        // Puedes mostrar un mensaje al usuario aquí
      } else {
        console.error("Error de registro:", error);
      }
    }
  };

  return (
    
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={styles.container}
    >
      <MyBlur />
      <SafeAreaView style={styles.container}>
        <ScrollView contentContainerStyle={styles.container}>
          <View style={styles.contentContainer}>
            <Text style={styles.title}>Registrate</Text>
            <TextInput
              value={formData.email}
              style={styles.input}
              placeholder="Tu Email"
              autoCapitalize="none"
              onChangeText={(text) => handleChangeText("email", text)}
              autoCorrect={false}
            />
            <TextInput
              value={formData.password}
              style={styles.input}
              placeholder="Tu Contraseña"
              autoCapitalize="none"
              onChangeText={(text) => handleChangeText("password", text)}
              autoCorrect={false}
              secureTextEntry={true}
            />

            <TextInput
              value={formData.confirmPassword}
              style={styles.input}
              placeholder="Repite Tu Contraseña"
              autoCapitalize="none"
              onChangeText={(text) => handleChangeText("confirmPassword", text)}
              autoCorrect={false}
              secureTextEntry={true}
            />

            <TextInput
              value={formData.firstName}
              style={styles.input}
              placeholder="Tu Nombre"
              autoCapitalize="none"
              onChangeText={(text) => handleChangeText("firstName", text)}
              autoCorrect={false}
            />
            <TextInput
              value={formData.lastName}
              style={styles.input}
              placeholder="Tu Apellido"
              autoCapitalize="none"
              onChangeText={(text) => handleChangeText("lastName", text)}
              autoCorrect={false}
            />
            <PhoneInput
              ref={(ref) => (this.phoneInput = ref)}
              initialCountry="us"
              onChangePhoneNumber={(value) => handleChangeText("phone", value)}
              textStyle={{ fontSize: 18 }}
            />
            <Text>Género:</Text>
            <Picker
              selectedValue={formData.gender}
              onValueChange={(itemValue) =>
                handleChangeText("gender", itemValue)
              }
            >
              <Picker.Item label="Selecciona" value="" />
              <Picker.Item label="Masculino" value="Masculino" />
              <Picker.Item label="Femenino" value="Femenino" />
              <Picker.Item label="Otro" value="Otro" />
            </Picker>
            <TextInput
              value={formData.diasEstrategia}
              style={styles.input}
              placeholder="Días Aplicando la Estrategia"
              onChangeText={(text) => handleChangeText("diasEstrategia", text)}
              keyboardType="numeric"
            />
            <>
              <TouchableOpacity
                onPress={() => {
                  handleRegistration();
                  // No es necesario navegar a SignIn aquí, ya que el redireccionamiento ocurre después de un registro exitoso
                }}
                style={styles.signInButton}
              >
                <Text style={{ color: "white", fontWeight: "bold" }}>Ok</Text>
              </TouchableOpacity>
            </>
          </View>
        </ScrollView>
      </SafeAreaView>
      
    </KeyboardAvoidingView>
  );
};
export default Registration;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    alignItems: "center",
    backgroundColor: "transparent",
    justifyContent: "center",
  },
  contentContainer: {
    paddingHorizontal: 30,
    marginTop: 50,
  },
  body: {
    padding: 20,
    textAlign: "center",
    fontSize: 16,
    color: "#6d6875",
    lineHeight: 30,
    fontWeight: "400",
  },
  title: {
    marginTop: 50,
    fontSize: 27,
    fontWeight: "700",
    lineHeight: 25,
    textAlign: "center",
    color: "#6d6875",
  },
  title2: {
    fontSize: 23,
    fontWeight: "500",
    textAlign: "center",
    color: "#6d6875",
  },
  buttonContainer: {
    flexDirection: "row",
    width: "100%",

    backgroundColor: "#DFE3E630",
    marginTop: 60,
  },
  button1: {
    flex: 1,
    alignItems: "center",
    backgroundColor: "#ffffff70",
    padding: 16,
    borderRadius: 6,
    borderWidth: 2,
    borderColor: "white",
    borderRadius: 16,
    marginHorizontal: 10,
  },
  button2: {
    flex: 1,
    alignItems: "center",
    padding: 16,
  },
  buttonsText: {
    fontWeight: "500",
    color: "#ff6e01",
  },
  input: {
    backgroundColor: "#F7F7F7",
    padding: 15,
    borderRadius: 10,
    marginBottom: 10,
  },
  signInButton: {
    backgroundColor: "#00b4d8",
    padding: 20,
    borderRadius: 12,
    alignItems: "center",
    marginVertical: 30,
    shadowColor: "#00b4d8",
    shadowOffset: {
      width: 0,
      height: 8,
    },
    shadowOpacity: 0.44,
    shadowRadius: 10.32,
  },
});
